import axios from "axios";
const PATIENT_API_URL = "http://localhost:8080";

class AppointmentDataService {
  retrieveAllPatients() {
    return axios.get(`${APPOINTMENT_API_URL}/getAllPatients`);
  }

  retrieveAppointment(id) {
    return axios.get(`${APPOINTMENT_API_URL}/Patient/${pid}`);
  }

  updateAppointment(slug, appointment) {
    return axios.put(
      `${APPOINTMENT_API_URL}/add-appointment/${slug}`,
      appointment
    );
  }

  addAppointment(appointment) {
    return axios.post(
      `${APPOINTMENT_API_URL}/patient-appointments`,
      appointment
    );
  }
}

export default new AppointmentDataService();
